package mirrorpatterns;
import java.util.Scanner;
public class pattern4 {
//	4444
//	333
//	22
//	1
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner s=new Scanner(System.in);
		int n=s.nextInt();
		int i=1;
		while(i<=n){
		int j=1;
		int k=n-i+1;
		while(j<=k){
		System.out.print(k);	
		j++;}
		System.out.println();
		i++;}}}
	


